mapper.select:
    SELECT 
		
		
		,(SELECT COUNT(id) FROM tb_article_comment WHERE article_id=tb_article.id)
		 AS commentCount
	FROM
		tb_article
	WHERE
		deleted=0
		
			and id=#{id}
		
		
			and title=#{title}
		
		
			and content=#{content}
		
		
			and category=#{category}
					
	ORDER BY create_time DESC limit #{pageNo} , #{pageSize}

SELECT 
	
	
	,(SELECT COUNT(id) FROM tb_article_comment WHERE article_id=tb_article.id)
	 AS commentCount 
FROM
	tb_article
WHERE
	deleted=0
	
		and id=#{id}
	
	
		and title=#{title}
	
	
		and category=#{category}

SELECT
FROM tb_article,rel_article_tag WHERE tb_article.deleted=0 AND rel_article_tag.article_id=tb_article.id AND rel_article_tag.tag_id=#{tagId}
ORDER BY tb_article.create_time DESC limit #{pageNo} , #{pageSize}

mapper.update:
UPDATE tb_article SET



	title=#{title},


	content=#{content},


	category=#{category}




	read_count=#{readCount}
		

	deleted=#{deleted}
	
WHERE id=#{id}
